INSERT INTO `style_tpl` VALUES ('9','1','home','book_full.html','全文阅读模板','<!DOCTYPE html PUBliC \\\"-//W3C//DTD XHTML 1.0 Transitional//EN\\\" \\\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\\\">\r\n<html xmlns=\\\"http://www.w3.org/1999/xhtml\\\">\r\n<head>\r\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=utf-8\\\" />\r\n<title>全文阅读 - {$book.book_name} - {&system.site_name}</title>\r\n<link rel=\\\"stylesheet\\\" type=\\\"text/css\\\" href=\\\"{$tplcms}css/full.css\\\">\r\n<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/jquery.js\\\"></script>\r\n<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/global.js\\\"></script>\r\n<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/book.js\\\"></script>\r\n</head>\r\n\r\n<body>\r\n	<div class=\\\"volume_contents\\\">\r\n		<div class=\\\"qwyd_spacer01\\\"></div>\r\n		<div class=\\\"qwyd_top\\\">\r\n			<a href=\\\"{$cmsroot}\\\">{&system.site_index}</a>　|　\r\n			<a href=\\\"javascript:;\\\" onclick=\\\"add_to_favor({$book.book_id}, 0);\\\">加入收藏</a>　|　\r\n			<a href=\\\"javascript:;\\\" onclick=\\\"add_recommend({$book.book_id});\\\">投推荐票</a>　|　\r\n			<a href=\\\"javascript:;\\\" onclick=\\\"open_favor();\\\">打开书架</a>　|　\r\n			<a href=\\\"{:BU($book, \\\'index\\\')}\\\">返回书页</a>　|　\r\n			<a href=\\\"{:BU($book, \\\'menu\\\')}\\\">返回书目</a>\r\n		</div>\r\n		<div class=\\\"qwyd_bt\\\">{$book.book_name}</div>\r\n		<div class=\\\"qwyd_auth\\\">\r\n			<div style=\\\"width: 180px; float: left\\\">作者：{$book.author}</div>\r\n			<div style=\\\"width:770px; float: right\\\">\r\n				<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/read_set.js\\\"></script>\r\n			</div>\r\n		</div>\r\n		<div id=\\\"txt_contents\\\">\r\n			<!--拷贝开始-->\r\n			<ul>\r\n		<volist name=\\\"list\\\" id=\\\"item\\\">\r\n			<if condition=\\\"$item.chapter_type eq 1\\\">\r\n				<if condition=\\\"$i neq 1\\\"><li class=\\\"mulu\\\"></li></if>\r\n				<li class=\\\"mulu01\\\">{$item.chapter_name}</li>\r\n			<else />\r\n				<li class=\\\"mulu_sub\\\"><a href=\\\"{:BU($book, \\\'full\\\')}#{$key}\\\">{$item.chapter_name}</a></li>\r\n			</if>\r\n		</volist>\r\n			</ul>\r\n	<volist name=\\\"list\\\" id=\\\"item\\\">\r\n		<if condition=\\\"$item.chapter_type eq 0\\\">\r\n			<a name=\\\"{$key}\\\"></a>\r\n			<div class=\\\"title\\\"><h1>{$item.chapter_name}</h1></div>\r\n			<div class=\\\"contents_txt\\\">{$item.chapter_detail|nl2br}</div>\r\n		</if>\r\n	</volist>\r\n			<!--拷贝结束-->\r\n		</div>\r\n		<!--bottom  start!!-->\r\n		<div class=\\\"qwyd_03\\\">&nbsp;</div>\r\n		<div class=\\\"thanks\\\">感谢作者受权发布　共同保护作者权益</div>\r\n		<!--bottom  stop!!-->\r\n	</div>\r\n</div>\r\n<script type=\\\"text/javascript\\\">$(function(){ initRead(true); }); </script>\r\n</body>\r\n</html>\r\n','1');
