INSERT INTO `style_tpl` VALUES ('16','1','home','review.html','评论主题模板','<!DOCTYPE html PUBliC \\\"-//W3C//DTD XHTML 1.0 Transitional//EN\\\" \\\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\\\">\r\n<html xmlns=\\\"http://www.w3.org/1999/xhtml\\\">\r\n<head>\r\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=utf-8\\\" />\r\n<title>{$review.subject} - {&system.site_name}</title>\r\n<meta name=\\\"keywords\\\" content=\\\"{&system.metakeywords}\\\" />\r\n<meta name=\\\"description\\\" content=\\\"{&system.metadescription}\\\" />\r\n<link rel=\\\"stylesheet\\\" type=\\\"text/css\\\" href=\\\"{$tplcms}css/default.css\\\" />\r\n<link rel=\\\"stylesheet\\\" type=\\\"text/css\\\" href=\\\"{$tplcms}css/books.css\\\">\r\n<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/jquery.js\\\"></script>\r\n<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/global.js\\\"></script>\r\n<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/book.js\\\"></script>\r\n</head>\r\n\r\n<body>\r\n<div id=\\\"layout\\\">\r\n	<div id=\\\"mainbody\\\">\r\n		<include file=\\\"./tpl/default/header.html\\\" />\r\n		<!--第二屏开始-->\r\n		<div class=\\\"fmy_hfq\\\">\r\n			<div class=\\\"fmy_hfq_top\\\">\r\n				<a href=\\\"{$cmsroot}\\\">{&system.site_index}</a> &gt; \r\n				<a href=\\\"{:BU($book,\\\'lists\\\')}\\\">{$book.sort_name}</a> &gt; \r\n				<a href=\\\"{:BU($book,\\\'index\\\')}\\\">{$book.book_name}</a> &gt; \r\n				书籍评论：{$review.subject}\r\n			</div>\r\n			<div class=\\\"fmy_hfq_bottom\\\">\r\n		        <div class=\\\"fmy_hfq_bottomnr\\\">\r\n					<div class=\\\"fmy_hfq_bottomnr_01\\\">\r\n						<div class=\\\"fmy_hfq_bottomnr_01_left\\\">\r\n							<a class=\\\"reply_url\\\" href=\\\"javascript:;\\\">回复</a> | \r\n							本主题共有 <font color=\\\"#ff0000\\\">{$review.reply_num}</font> 条跟帖\r\n						</div>\r\n						<div class=\\\"fmy_hfq_bottomnr_01_right\\\">\r\n							<a href=\\\"{:BU($book,\\\'index\\\')}\\\">返回小说首页</a>\r\n						</div>\r\n					</div>\r\n					<div class=\\\"fmy_hfq_bottomnr_02\\\">\r\n						<span id=\\\"sptitle_{$review.review_id}\\\">\r\n							{$review.subject}&nbsp;&nbsp;\r\n							<if condition=\\\"$review.if_good eq 1\\\"><font color=\\\"#ff0000\\\">[精华]</font>&nbsp;</if>\r\n							<if condition=\\\"$review.if_top eq 1\\\"><font color=\\\"#ff0000\\\">[置顶]</font>&nbsp;</if>\r\n						</span>\r\n						<span class=\\\"top_name\\\">楼主</span>\r\n					</div>\r\n					<div class=\\\"fmy_hfq_bottomnr_03\\\">{$review.detail|nl2br}</div>\r\n					<div class=\\\"fmy_hfq_bottomnr_04\\\">\r\n						<div class=\\\"fmy_hfq_bottomnr_04_left\\\">\r\n							<font color=\\\"#747474\\\">发表人：{$review.poster}</font>\r\n						</div>\r\n						<div class=\\\"fmy_hfq_bottomnr_04_right\\\">\r\n							<font color=\\\"#747474\\\">发表时间：{$review.post_time|date=\\\"Y-m-d H:i:s\\\",###}</font>\r\n							<a href=\\\"javascript:;\\\" class=\\\"reply_url\\\">回复</a>\r\n						</div>\r\n					</div>\r\n					<div id=\\\"replycontent\\\">\r\n					<volist name=\\\"list\\\" id=\\\"item\\\">\r\n						<div class=\\\"fmy_hfq_bottomnr_05\\\">\r\n							<div class=\\\"fmy_hfq_bottomnr_05_left\\\">{$item.subject}</div>\r\n							<div class=\\\"fmy_hfq_bottomnr_05_center\\\">\r\n							<present name=\\\"Think.cookie.admin_id\\\">\r\n								<a href=\\\"javascript:;\\\" onclick=\\\"reply_del({$item.reply_id})\\\">删除</a>\r\n								<if condition=\\\"$item.if_display eq 1\\\">\r\n								<a href=\\\"javascript:;\\\" onclick=\\\"reply_show({$item.reply_id},0)\\\">屏蔽</a>\r\n								<else />\r\n								<a href=\\\"javascript:;\\\" onclick=\\\"reply_show({$item.reply_id},1)\\\">显示</a>\r\n								</if>\r\n							<else />\r\n								&nbsp;\r\n							</present>\r\n							</div>\r\n							<div class=\\\"fmy_hfq_bottomnr_05_right\\\">{$i} 楼</div>\r\n						</div>\r\n						<div class=\\\"fmy_hfq_bottomnr_06\\\">\r\n						<if condition=\\\"$item.if_display eq 1\\\">\r\n							{$item.detail|nl2br}\r\n						<else />\r\n							<span>该回复已被屏蔽</span>\r\n						</if>\r\n						</div>\r\n						<div class=\\\"fmy_hfq_bottomnr_07\\\">\r\n							<div class=\\\"fmy_hfq_bottomnr_07_left\\\">\r\n								<font color=\\\"#747474\\\">发表人：{$item.poster}</font>\r\n							</div>\r\n							<div class=\\\"fmy_hfq_bottomnr_07_right\\\">\r\n								<font color=\\\"#747474\\\">发表时间：{$item.post_time|date=\\\"Y-m-d H:i:s\\\",###}</font>\r\n								<a href=\\\"javascript:;\\\" class=\\\"reply_url\\\">回复</a>\r\n							</div>\r\n						</div>\r\n					</volist>\r\n					</div>\r\n					<div class=\\\"fmy_hfq_bottomnr_01\\\">\r\n						<div class=\\\"fmy_hfq_bottomnr_01_left\\\">\r\n							<a class=\\\"reply_url\\\" href=\\\"javascript:;\\\">回复</a> | \r\n							本主题共有 {$page}\r\n						</div>\r\n						<div class=\\\"fmy_hfq_bottomnr_01_right\\\">\r\n							<a href=\\\"{:BU($book,\\\'index\\\')}\\\">返回小说首页</a>\r\n						</div>\r\n					</div>\r\n					<div class=\\\"break_04\\\"></div>\r\n					<div style=\\\"border-top: 0px\\\" class=\\\"tlq_answer\\\">\r\n						<div class=\\\"break_04\\\"></div>\r\n						<div><textarea id=\\\"sp_content_{$review.review_id}\\\" class=\\\"tlq_answer_lynr need_login\\\"></textarea></div>\r\n						<div class=\\\"break_01\\\"></div>\r\n						<div class=\\\"tlq_answer_hf\\\">\r\n							<div class=\\\"tlq_answer_hf_l\\\">\r\n								<a href=\\\"javascript:;\\\" id=\\\"submitsplink\\\" onclick=\\\"quick_reply({$item.review_id});\\\">回复</a>\r\n							</div>\r\n							<div class=\\\"tlq_answer_hf_r\\\">\r\n								<a href=\\\"javascript:;\\\" onclick=\\\"$(\\\'#sp_content\\\').attr(\\\'value\\\',\\\'\\\');\\\">取消</a>\r\n							</div>\r\n						</div>\r\n					</div>\r\n					<div class=\\\"break_04\\\"></div>\r\n		        </div>\r\n			</div>\r\n		</div>\r\n		<!--封面页回复区结束-->\r\n		<div class=\\\"break_09\\\"></div>\r\n		<include file=\\\"./tpl/default/footer.html\\\" />\r\n	</div>\r\n</div>\r\n<script type=\\\"text/javascript\\\">\r\n	$(\\\".reply_url\\\").click(function(){\r\n		$(\\\'#sp_content_{$review.review_id}\\\').focus();\r\n	});\r\n	$(\\\".need_login\\\").focus(function(){ show_login(); });\r\n</script>\r\n</body>\r\n</html>\r\n\r\n','1');
INSERT INTO `style_tpl` VALUES ('17','1','home','review_lists.html','评论列表','<volist name=\\\"list\\\" id=\\\"item\\\">\r\n<!--书评 开始-->\r\n<div class=\\\"break_01\\\">&nbsp;</div>\r\n<div class=\\\"pl_question\\\">\r\n	<p>\r\n		<a href=\\\"{$cmsroot}index.php?s=review/index/id/{$item.review_id}\\\" target=\\\"_blank\\\" id=\\\"sptitle_{$item.review_id}\\\">{$item.subject}</a>\r\n		<span class=\\\"flag\\\">\r\n			<if condition=\\\"$item.if_good eq 1\\\">　[精华]</if>\r\n			<if condition=\\\"$item.if_top eq 1\\\">　[置顶]</if>\r\n			<if condition=\\\"$item.if_lock eq 1\\\">　[锁定]</if>\r\n		</span>\r\n	</p>\r\n</div>\r\n<div class=\\\"pl_questionnr\\\">\r\n	<if condition=\\\"$item.if_check eq 1\\\">{$item.detail}<else />评论审核中...</if>\r\n</div>\r\n<div class=\\\"break_03\\\">&nbsp;</div>\r\n<div class=\\\"fabiaonr\\\">\r\n	发表人：<span class=\\\"s1\\\">{$item.poster}</span>　\r\n	发表日期：<span class=\\\"s2\\\">{$item.post_time|date=\\\"Y-m-d H:i:s\\\",###}</span>　\r\n	点击数：<span class=\\\"s2\\\">{$item.view_num}</span>　\r\n	回复数：<span class=\\\"s2\\\" id=\\\"sp_num_{$item.review_id}\\\">{$item.reply_num}</span>\r\n</div>\r\n<div class=\\\"fabiaonr001\\\">\r\n	<a href=\\\"javascript:;\\\" onclick=\\\"<if condition=\\\"$item.if_lock eq 1\\\">reply_lock();<else />show_quick_reply({$item.review_id});</if>\\\"><font color=\\\"#1573CA\\\">[ 回复 ]</font></a>&nbsp;\r\n	<a href=\\\"javascript:;\\\" onclick=\\\"$(\\\'#sp_title\\\').focus();\\\"><font color=\\\"#1573CA\\\">[ 快速发表 ]</font></a>\r\n</div>\r\n<div id=\\\"spreply_{$item.review_id}\\\" style=\\\"display: none;\\\">\r\n	<div class=\\\"break_04\\\">&nbsp;</div>\r\n	<div class=\\\"tlq_answer\\\">\r\n		<div class=\\\"break_04\\\">&nbsp;</div>\r\n		<div><textarea id=\\\"sp_content_{$item.review_id}\\\" class=\\\"tlq_answer_lynr\\\"></textarea></div>\r\n		<div class=\\\"break_01\\\">&nbsp;</div>\r\n		<div class=\\\"tlq_answer_hf\\\">\r\n			<div class=\\\"tlq_answer_hf_l\\\">\r\n				<a href=\\\"javascript:;\\\" onclick=\\\"quick_reply({$item.review_id},1);\\\">回 复</a>\r\n			</div>\r\n			<div class=\\\"tlq_answer_hf_r\\\">\r\n				<a href=\\\"javascript:;\\\" onclick=\\\"$(\\\'#spreply_{$item.review_id}\\\').hide();\\\">取 消</a>\r\n			</div>\r\n		</div>\r\n	</div>\r\n	<div class=\\\"break_04\\\">&nbsp;</div>\r\n</div>\r\n</volist>\r\n','1');
