INSERT INTO `style_tpl` VALUES ('22','1','user','user_edit.html','用户个人信息修改模板','<!DOCTYPE html PUBliC \\\"-//W3C//DTD XHTML 1.0 Transitional//EN\\\" \\\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\\\">\n<html xmlns=\\\"http://www.w3.org/1999/xhtml\\\">\n<head>\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=utf-8\\\" />\n<title>个人信息 - {&system.site_name}</title>\n<link rel=\\\"stylesheet\\\" type=\\\"text/css\\\" href=\\\"{$tplcms}css/default.css\\\" />\n<link rel=\\\"stylesheet\\\" type=\\\"text/css\\\" href=\\\"{$tplcms}css/user.css\\\">\n<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/jquery.js\\\"></script>\n<script type=\\\"text/javascript\\\" src=\\\"{$tplcms}js/global.js\\\"></script>\n</head>\n\n<body>\n<div id=\\\"layout\\\">\n	<div id=\\\"mainbody\\\">\n		<include file=\\\"./tpl/default/header.html\\\" />\n		<div id=\\\"main\\\">\n			<div id=\\\"mainleft\\\">\n				<div><img src=\\\"{$tplcms}images/hyzx_djsc_01.gif\\\" ></div>\n				<div class=\\\"tit\\\">\n					<img src=\\\"{$tplcms}images/hyzx_right_icon_08.gif\\\" class=\\\"ico\\\" />个人信息\n				</div>\n				<div><img src=\\\"{$tplcms}images/hyzx_djsc_03.gif\\\" /></div>\n				\n				<form name=\\\"register\\\" action=\\\"?s=user/user/do_update\\\" method=\\\"post\\\">\n					<input type=\\\"hidden\\\" name=\\\"id\\\" value=\\\"{$user.id}\\\" />\n					<div class=\\\"reg_info\\\">\n						<div class=\\\"rinfo\\\">\n							<span class=\\\"rleft\\\">帐号：</span>\n							<span class=\\\"rright\\\">{$user.user_name}</span>\n						</div>\n						<div class=\\\"rinfo\\\">\n							<span class=\\\"rleft\\\">真实姓名：</span>\n							<span class=\\\"rright\\\"><input type=\\\"text\\\" name=\\\"real_name\\\" value=\\\"{$info.real_name}\\\" /></span>\n						</div>\n						<div class=\\\"rinfo\\\">\n							<span class=\\\"rleft\\\">您的昵称：</span>\n							<span class=\\\"rright\\\"><input type=\\\"text\\\" name=\\\"nickname\\\" value=\\\"{$user.nickname}\\\" /></span>\n						</div>\n						<div c